dellstore2=# CREATE OR REPLACE FUNCTION orders_update_trigger()                                                                                                                                             RETURNS TRIGGER AS $$                                                                                                                                                                                       BEGIN                                                                                                                                                                                                           DELETE FROM orders WHERE OLD.orderid=orderid;                                                                                                                                                               INSERT INTO orders values(NEW.*);                                                                                                                                                                           RETURN NULL;                                                                                                                                                                                            END;                                                                                                                                                                                                        $$                                                                                                                                                                                                          LANGUAGE plpgsql;
CREATE FUNCTION
dellstore2=# CREATE TRIGGER update_orders                                                                                                                                                                        BEFORE UPDATE ON orders                                                                                                                                                                                     FOR EACH ROW                                                                                                                                                                                                EXECUTE PROCEDURE orders_update_trigger();
CREATE TRIGGER

